import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import AddTaskModal from './AddTaskModal';
import TaskModal from './TaskModal';
import Task from './Task';

const TaskPage = () => {
  const { projectId } = useParams();
  const [tasks, setTasks] = useState([]);
  const [isAddTaskModalOpen, setAddTaskModalOpen] = useState(false);
  const [isTaskModalOpen, setTaskModalOpen] = useState(false);
  const [selectedTask, setSelectedTask] = useState(null);

  const addTaskToList = (newTask) => {
    setTasks([...tasks, newTask]);
  };

  const openAddTaskModal = () => setAddTaskModalOpen(true);
  const closeAddTaskModal = () => setAddTaskModalOpen(false);

  const openTaskModal = (task) => {
    setSelectedTask(task);
    setTaskModalOpen(true);
  };
  const closeTaskModal = () => setTaskModalOpen(false);

  return (
    <div>
      <h1>Tasks for Project {projectId}</h1>
      <button onClick={openAddTaskModal}>Add Task</button>
      <Task projectId={projectId} />
      <AddTaskModal isModalOpen={isAddTaskModalOpen} closeModal={closeAddTaskModal} addTaskToList={addTaskToList} />
      {selectedTask && <TaskModal isModalOpen={isTaskModalOpen} closeModal={closeTaskModal} task={selectedTask} />}
    </div>
  );
};

export default TaskPage;
