import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import TaskCard from './TaskCard';
import AddTaskModal from './AddTaskModal';
import EditTaskModal from './EditTaskModal';

const TaskPage = () => {
  const { projectId } = useParams();
  const [tasks, setTasks] = useState([]);
  const [selectedTask, setSelectedTask] = useState(null);
  const [isAddModalOpen, setAddModalOpen] = useState(false);
  const [isEditModalOpen, setEditModalOpen] = useState(false);

  const addTask = (task) => setTasks([...tasks, task]);
  const editTask = (updatedTask) => setTasks(tasks.map((t) => (t.id === updatedTask.id ? updatedTask : t)));
  const deleteTask = (id) => setTasks(tasks.filter((t) => t.id !== id));

  return (
    <div className="task-page">
      <h1>Tasks for Project {projectId}</h1>
      <button onClick={() => setAddModalOpen(true)}>Add Task</button>
      <div className="task-list">
        {tasks.map((task) => (
          <TaskCard
            key={task.id}
            task={task}
            openTaskModal={() => setSelectedTask(task)}
            openEditTaskModal={() => { setSelectedTask(task); setEditModalOpen(true); }}
            deleteTask={deleteTask}
          />
        ))}
      </div>
      <AddTaskModal isOpen={isAddModalOpen} onClose={() => setAddModalOpen(false)} onAdd={addTask} />
      {selectedTask && (
        <EditTaskModal
          isOpen={isEditModalOpen}
          onClose={() => setEditModalOpen(false)}
          task={selectedTask}
          onEdit={editTask}
        />
      )}
    </div>
  );
};

export default TaskPage;
