import React from 'react';

const ProjectCard = ({ project, onSelect }) => (
  <div className="project-card p-4 bg-white rounded-lg shadow-sm hover:shadow-lg transition-shadow duration-300" onClick={() => onSelect(project._id)}>
    <h3 className="text-xl font-semibold mb-2">{project.title}</h3>
    <p className="text-gray-600 mb-4">{project.description}</p>
    <div className="project-actions flex justify-center">
      <button className="text-blue-500 hover:text-blue-700" onClick={(e) => { e.stopPropagation(); onSelect(project._id); }}>View Tasks</button>
    </div>
  </div>
);

export default ProjectCard;
