import React from "react";
import Navbar from "./Navbar";

const Home = () => {
  return (
    <div>
      <Navbar />
      <div className="px-4 pt-5 my-5 text-center border-bottom">
        <h1 className="display-4 fw-bold text-body-emphasis">Welcome to the Project Management Tool</h1>
        <div className="col-lg-6 mx-auto">
          <p className="lead mb-4">
            Manage your projects efficiently with our tool. Create, edit, and track tasks seamlessly. Collaborate with your team and stay on top of your project timelines.
          </p>
        </div>
        <div className="overflow-hidden" style="max-height: 30vh;">
          <div className="container px-5">
            <img src="/path/to/your/image/welcome.svg" className="img-fluid border rounded-3 shadow-lg mb-4" alt="Welcome Image" width="700" height="500" loading="lazy" />
          </div>
        </div>
      </div>
    </div>
  );
};

export default Home;
