import React, { useState, useEffect } from 'react';
import { Card, Button } from 'react-bootstrap';
import { Link } from 'react-router-dom';

const Dashboard = () => {
  const [projects, setProjects] = useState([]);

  useEffect(() => {
    // Mock API call to fetch projects
    const mockProjects = [
      { _id: '1', title: 'Project 1', description: 'Description 1' },
      { _id: '2', title: 'Project 2', description: 'Description 2' },
    ];
    setProjects(mockProjects);
  }, []);

  return (
    <div className="relative flex w-full pt-10">
      <div className="w-1/4">
        {/* Sidebar placeholder */}
      </div>
      <div className="relative w-3/4 z-10 flex flex-col items-start px-4">
        {projects.map((project) => (
          <Card key={project._id} className="mb-3 shadow-sm w-full">
            <Card.Body>
              <Card.Title>{project.title}</Card.Title>
              <Card.Text>{project.description}</Card.Text>
              <Link to={`/${project._id}`}>
                <Button variant="primary">View Project</Button>
              </Link>
            </Card.Body>
          </Card>
        ))}
      </div>
      <div className="absolute top-0 right-0 w-1/4 h-full flex flex-col items-center justify-center">
        <img src="/image/welcome.svg" className="w-full max-w-xs" alt="Welcome" />
        <h1 className="text-lg text-gray-600 mt-4">Select or create a new project</h1>
      </div>
    </div>
  );
};

export default Dashboard;
