import React, { useState, useEffect } from 'react';
import { Card, Button } from 'react-bootstrap';
import { Link } from 'react-router-dom';

const Home = () => {
  const [projects, setProjects] = useState([]);

  useEffect(() => {
    // Mock API call to fetch projects
    const mockProjects = [
      { _id: '1', title: 'Project 1', description: 'Description 1' },
      { _id: '2', title: 'Project 2', description: 'Description 2' },
    ];
    setProjects(mockProjects);
  }, []);

  return (
    <div className="relative flex flex-col items-center w-full pt-10">
      <div className="absolute top-0 left-0 w-full h-full flex flex-col items-center justify-center">
        <img src="/image/welcome.svg" className="w-5/12" alt="Welcome" />
        <h1 className="text-lg text-gray-600">Select or create a new project</h1>
      </div>
      <div className="relative w-5/12 mb-4 z-10">
        {projects.map((project) => (
          <Card key={project._id} className="mb-3 shadow-sm">
            <Card.Body>
              <Card.Title>{project.title}</Card.Title>
              <Card.Text>{project.description}</Card.Text>
              <Link to={`/${project._id}`}>
                <Button variant="primary">View Project</Button>
              </Link>
            </Card.Body>
          </Card>
        ))}
      </div>
    </div>
  );
};

export default Home;
