import React, { useEffect, useState } from 'react';
import { Modal, Button, Form } from 'react-bootstrap';
import axios from 'axios';
import toast from 'react-hot-toast';

const AddProjectModal = ({ isModalOpen, closeModal, edit = false, id = null }) => {
  const [title, setTitle] = useState('');
  const [desc, setDesc] = useState('');

  useEffect(() => {
    if (edit && isModalOpen) {
      axios.get(`http://localhost:9000/project/${id}`)
        .then((res) => {
          setTitle(res.data[0].title);
          setDesc(res.data[0].description);
        })
        .catch((error) => {
          toast.error('Something went wrong');
        });
    }
  }, [isModalOpen]);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!edit) {
      axios.post('http://localhost:9000/project/', { title, description: desc })
        .then((res) => {
          closeModal();
          const customEvent = new CustomEvent('projectUpdate', { detail: { ...res.data } });
          document.dispatchEvent(customEvent);
          toast.success('Project created successfully');
          setTitle('');
          setDesc('');
        })
        .catch((error) => {
          if (error.response.status === 422) {
            toast.error(error.response.data.details[0].message);
          } else {
            toast.error('Something went wrong');
          }
        });
    } else {
      axios.put(`http://localhost:9000/project/${id}`, { title, description: desc })
        .then((res) => {
          closeModal();
          const customEvent = new CustomEvent('projectUpdate', { detail: { ...res.data } });
          document.dispatchEvent(customEvent);
          toast.success('Project updated successfully');
          setTitle('');
          setDesc('');
        })
        .catch((error) => {
          if (error.response.status === 422) {
            toast.error(error.response.data.details[0].message);
          } else {
            toast.error('Something went wrong');
          }
        });
    }
  };

  return (
    <Modal show={isModalOpen} onHide={closeModal} centered>
      <Modal.Header closeButton>
        <Modal.Title>{edit ? 'Edit Project' : 'Create Project'}</Modal.Title>
      </Modal.Header>
      <Form onSubmit={handleSubmit}>
        <Modal.Body>
          <Form.Group className="mb-3" controlId="formTitle">
            <Form.Label>Title</Form.Label>
            <Form.Control
              type="text"
              placeholder="Project title"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
            />
          </Form.Group>
          <Form.Group className="mb-3" controlId="formDescription">
            <Form.Label>Description</Form.Label>
            <Form.Control
              as="textarea"
              rows={3}
              placeholder="Project description"
              value={desc}
              onChange={(e) => setDesc(e.target.value)}
            />
          </Form.Group>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={closeModal}>
            Cancel
          </Button>
          <Button variant="primary" type="submit">
            {edit ? 'Save Changes' : 'Create'}
          </Button>
        </Modal.Footer>
      </Form>
    </Modal>
  );
};

export default AddProjectModal;
